import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Game from "./components/Game";
import Home from "./Pages/Home";
import Leaderboard from "./components/Leaderboard";
import MainMenu from "./components/MainMenu"; // Import MainMenu Component
import ChooseDifficulty from "./components/ChooseDifficulty";

function App() {
  const [difficulty, setDifficulty] = useState("");

  const handleDifficultySelect = (selectedDifficulty) => {
    setDifficulty(selectedDifficulty);
    // You can now use the difficulty state for navigation or any other logic
  };
  return (
    <Router>
      <Routes>
        {/* Home page (for initial landing, before login/signup) */}
        <Route path="/" element={<Home />} />

        {/* Login and Signup Routes */}
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route 
          path="/choose-difficulty" 
          element={<ChooseDifficulty onSelect={handleDifficultySelect} />} 
        />


        {/* Main Menu Route (for after login) */}
        <Route path="/mainmenu" element={<MainMenu />} />

        {/* Game and Leaderboard Routes */}
        <Route path="/game" element={<Game />} />
        <Route path="/leaderboard" element={<Leaderboard />} />
      </Routes>
    </Router>
  );
}

export default App;
