// Login.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import "../styles/Login.css";
import { ArrowLeftCircle } from "lucide-react";
import bananaHumanIcon from "../assets/images/banana-human.png";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/users/login", {
        username,
        password,
      });

      localStorage.setItem("token", res.data.token);
      localStorage.setItem("userId", res.data.userId);

      Swal.fire({
        title: "Login Successful!",
        text: "Welcome back, player!",
        icon: "success",
        background: "#1e1e1e",
        color: "#fff",
        confirmButtonText: "Start Game",
        confirmButtonColor: "#00ff00",
        backdrop: "rgba(0, 255, 0, 0.2)",
      });

      navigate("/main-menu");
    } catch (err) {
      Swal.fire({
        title: "Access Denied!",
        text: "Invalid username or password. Try again!",
        icon: "error",
        background: "#1e1e1e",
        color: "#fff",
        confirmButtonText: "Retry",
        confirmButtonColor: "#ff0000",
        backdrop: "rgba(255, 0, 0, 0.3)",
      });
    }
  };

  return (
    <div className="login-container">
      <button className="go-back-button" onClick={() => navigate("/")}> 
        <ArrowLeftCircle size={32} strokeWidth={3} color="white" />
      </button>

      <div className="overlay"></div>
      <img src={bananaHumanIcon} alt="Banana Human" className="banana-icon" />
      <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Enter Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            className="cartoonic-input"
          />
          <input
            type="password"
            placeholder="Enter Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="cartoonic-input"
          />
          <button type="submit" className="cartoonic-button-3d">Login</button>
        </form>
        <p className="signup-text">Don't have an account?</p>
        <span className="spans" onClick={() => navigate("/signup")}>Sign Up</span>
      </div>
    </div>
  );
}

export default Login;
