import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/MainMenu.css";
import { ArrowLeftCircle, Settings, Music } from "lucide-react";
import profileImage from "../assets/images/prfl.webp";
import { MusicContext } from "../App"; // Import MusicContext

function MainMenu() {
  const navigate = useNavigate();
  const playerName = localStorage.getItem("username") || "Player";
  const { isMusicOn, setIsMusicOn } = useContext(MusicContext);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  return (
    <div className="main-menu-cartoonic">
      <div className="overlayM"></div>
      <button className="go-back-button" onClick={() => navigate("/")}>
        <ArrowLeftCircle size={32} strokeWidth={3} color="white" />
      </button>

      <div className="profile-container">
        <img src={profileImage} alt="Profile" className="profile-icon" />
        <p className="player-name">{playerName}</p>
      </div>

      <h2 className="cartoonic-title"></h2>
      <button className="cartoonic-button-3dd leaderboard-btn" onClick={() => navigate("/leaderboard")}>
        üèÜ LEADERBOARD
      </button>

      <button className="cartoonic-button-3dd play-btn" onClick={() => {
        startMusic(); // Start music when clicking PLAY
        navigate("/choose-difficulty");
      }}>
        üöÄ PLAY
      </button>

      <button className="cartoonic-button-3dd back-home-btn" onClick={() => navigate("/")}>
        üè° QUIT
      </button>
      <button className="cartoonic-button-3dd settings-btn" onClick={() => setIsSettingsOpen(true)}>
        ‚öôÔ∏è SETTINGS
      </button>

     {isSettingsOpen && (
    <div className="settings-modal">
      <div className="settings-content">
      <h3 className="settings-header">Settings</h3>
        <div className="music-icon">üéµ</div>
        
        
        <div className="settings-option">
          <label className="switch">
          <button onClick={() => setIsMusicOn(!isMusicOn)}>
        {isMusicOn ? "üîä Music On" : "üîá Music Off"}
      </button>
            <span className="slider round"></span>
          </label>
        </div>

        <button className="close-settings-btn" onClick={() => setIsSettingsOpen(false)}>
          Close
        </button>
      </div>
    </div>
)}

    </div>
  );
}

export default MainMenu;
