import axios from "axios";

const API_URL = "http://localhost:5000/api";

// Fetch a new math question
export const fetchQuestion = async () => {
  try {
    const res = await axios.get(`${API_URL}/banana`);
    return res.data;
  } catch (error) {
    console.error("Error fetching question", error);
    throw error;
  }
};

// Save user score
// gameService.js
export const saveScore = async (delta) => {
  const token = localStorage.getItem("token");
  const userId = localStorage.getItem("userId");
  const difficulty = localStorage.getItem("selectedDifficulty") || "Easy";

  const response = await fetch(`${BASE_URL}/score/save`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: token
    },
    body: JSON.stringify({ userId, score: delta, difficulty }),
  });
  return await response.json();
};

export const fetchTotalScore = async (userId) => {
    const response = await axios.get(`${API_URL}/score/total-score/${userId}`);
    return response.data;
  };
