import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { ArrowLeftCircle, Settings, Music } from "lucide-react";
import profileImage from "../assets/images/prfl.webp";
import { MusicContext } from "../App"; // Import Music Context

function MainMenu() {
  const navigate = useNavigate();
  const { isMusicOn, setIsMusicOn, startMusic } = useContext(MusicContext);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  return (
    <div className="main-menu-cartoonic">
      <button className="cartoonic-button-3dd play-btn" onClick={() => {
        startMusic(); // Start music when clicking PLAY
        navigate("/choose-difficulty");
      }}>
        üöÄ PLAY
      </button>

      <button className="cartoonic-button-3dd settings-btn" onClick={() => setIsSettingsOpen(true)}>
        ‚öôÔ∏è SETTINGS
      </button>

      {isSettingsOpen && (
        <div className="settings-modal">
          <h3>Settings</h3>
          <div className="settings-option">
            <Music size={24} /> 
            <label className="switch">
              <input type="checkbox" checked={isMusicOn} onChange={() => setIsMusicOn(!isMusicOn)} />
              <span className="slider round"></span>
            </label>
          </div>
          <button className="close-settings-btn" onClick={() => setIsSettingsOpen(false)}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}

export default MainMenu;
