import React, { useState } from "react";
import { useNavigate } from "react-router-dom";  // Import useNavigate
import "../styles/ChooseDifficulty.css";  // Import the ChooseDifficulty CSS

function ChooseDifficulty({ onSelect }) { 
  const [difficulty, setDifficulty] = useState("Easy");
  const navigate = useNavigate(); // Initialize the navigation hook

  const handleSubmit = () => {
    // Save the selected difficulty in localStorage and pass it via onSelect
    localStorage.setItem("selectedDifficulty", difficulty);
    onSelect(difficulty);  // This calls the parent handler
    navigate("/game");  // Navigate to the game page
  };

  return (
    <div className="modal">
      <h2>Choose Difficulty</h2>
      <form>
        <label>
          <input
            type="radio"
            value="Easy"
            checked={difficulty === "Easy"}
            onChange={(e) => setDifficulty(e.target.value)}
          />
          Easy
        </label>
        <br />
        <label>
          <input
            type="radio"
            value="Medium"
            checked={difficulty === "Medium"}
            onChange={(e) => setDifficulty(e.target.value)}
          />
          Medium
        </label>
        <br />
        <label>
          <input
            type="radio"
            value="Hard"
            checked={difficulty === "Hard"}
            onChange={(e) => setDifficulty(e.target.value)}
          />
          Hard
        </label>
      </form>
      <button onClick={handleSubmit}>Okay</button>
    </div>
  );
}

export default ChooseDifficulty;
