import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../styles/MainMenu.css";
import { ArrowLeftCircle, Settings, Music } from "lucide-react";
import profileImage from "../assets/images/prfl.webp"; // Profile icon
import backgroundMusic from "../assets/audio/background.mp3"; // Background music file

function MainMenu() {
  const navigate = useNavigate();
  const location = useLocation();

  const playerName = localStorage.getItem("username") || "Player";
  
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isMusicOn, setIsMusicOn] = useState(true);
  const [audio] = useState(new Audio(backgroundMusic));

  useEffect(() => {
    if (isMusicOn) {
      audio.loop = true;
      audio.play().catch((error) => console.error("Audio playback failed", error));
    } else {
      audio.pause();
    }

    return () => {
      audio.pause();
    };
  }, [isMusicOn]);

  // Stop music when leaving all game-related pages
  useEffect(() => {
    const gamePages = ["/mainmenu", "/choose-difficulty", "/game", "/leaderboard"];
    if (!gamePages.includes(location.pathname)) {
      audio.pause();
    }
  }, [location.pathname, audio]);

  return (
    <div className="main-menu-cartoonic">
      <div className="overlayM"></div>
      <button className="go-back-button" onClick={() => navigate("/")}>
        <ArrowLeftCircle size={32} strokeWidth={3} color="white" />
      </button>

      {/* Profile Icon and Player Name */}
      <div className="profile-container">
        <img src={profileImage} alt="Profile" className="profile-icon" />
        <p className="player-name">{playerName}</p>
      </div>

      <h2 className="cartoonic-title"></h2>
      <button className="cartoonic-button-3dd leaderboard-btn" onClick={() => navigate("/leaderboard")}>
        üèÜ LEADERBOARD
      </button>
      <button className="cartoonic-button-3dd play-btn" onClick={() => navigate("/choose-difficulty")}>
        üöÄ PLAY
      </button>
      <button className="cartoonic-button-3dd settings-btn" onClick={() => setIsSettingsOpen(true)}>
        ‚öôÔ∏è SETTINGS
      </button>
      <button className="cartoonic-button-3dd back-home-btn" onClick={() => navigate("/")}>
        üè° QUIT
      </button>
      

      {/* Settings Form */}
      {isSettingsOpen && (
        <div className="settings-modal">
          <div className="settings-content">
            <h3>Settings</h3>
            <div className="settings-option">
              <Music size={24} /> 
              <label className="switch">
                <input type="checkbox" checked={isMusicOn} onChange={() => setIsMusicOn(!isMusicOn)} />
                <span className="slider round"></span>
              </label>
            </div>
            <button className="close-settings-btn" onClick={() => setIsSettingsOpen(false)}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default MainMenu;
