import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import { ArrowLeftCircle } from "lucide-react";
import bananaHumanIcon from "../assets/images/banana-human.png";
import "../styles/Login.css";
import { loginUser } from "../services/authApi"; // ✅ Import API

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await loginUser(username, password); // ✅ Use API function
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("userId", res.data.userId);

      Swal.fire({
        title: "Login Successful!",
        text: "Welcome back, player!",
        icon: "success",
        confirmButtonColor: "#00ff00",
      });

      navigate("/mainmenu");
    } catch (err) {
      Swal.fire({
        title: "Access Denied!",
        text: "Invalid username or password. Try again!",
        icon: "error",
        confirmButtonColor: "#ff0000",
      });
    }
  };

  return (
    <div className="login-container">
      <button className="go-back-button" onClick={() => navigate("/")}>
        <ArrowLeftCircle size={32} strokeWidth={3} color="white" />
      </button>
      <div className="overlay"></div>
      <img src={bananaHumanIcon} alt="Banana Human" className="banana-icons" />
      <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
          <input type="text" placeholder="Enter Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
          <input type="password" placeholder="Enter Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <button type="submit" className="cartoonic-button-3d">Login</button>
        </form>
        <p className="signup-text">Don't have an account?</p>
        <span className="spans" onClick={() => navigate("/signup")}>Sign Up</span>
      </div>
    </div>
  );
}

export default Login;
