// ChooseDifficulty.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/ChooseDifficulty.css";
import bananaIcon from "../assets/images/banana-human.png";

function ChooseDifficulty({ onSelect }) {
  const [difficulty, setDifficulty] = useState("Easy");
  const navigate = useNavigate();

  const handleSubmit = () => {
    localStorage.setItem("selectedDifficulty", difficulty);
    if (onSelect) onSelect(difficulty);
    navigate("/game");
  };

  return (
    <div className="modalC-cartoonic">
      <div className="overlayC"></div>
      <img src={bananaIcon} alt="Banana Icon" className="banana-mascot" />
      <div className="modal-cartoonic">
        <h2>üéØ Choose Difficulty üéØ</h2>
        <form>
          <label>
            <input
              type="radio"
              value="Easy"
              checked={difficulty === "Easy"}
              onChange={(e) => setDifficulty(e.target.value)}
            />
            üçå Easy
          </label>
          <label>
            <input
              type="radio"
              value="Medium"
              checked={difficulty === "Medium"}
              onChange={(e) => setDifficulty(e.target.value)}
            />
            üöÄ Medium
          </label>
          <label>
            <input
              type="radio"
              value="Hard"
              checked={difficulty === "Hard"}
              onChange={(e) => setDifficulty(e.target.value)}
            />
            üî• Hard
          </label>
        </form>
        <button className="cartoonic-button-3ddd" onClick={handleSubmit}>‚úÖ Confirm</button>
      </div>
    </div>
  );
}

export default ChooseDifficulty;
