import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import '../styles/Game.css';  // Import the ChooseDifficulty CSS
import bananaImage from './images/banana.jpeg';  // Import the image


function Game() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [timeLeft, setTimeLeft] = useState(30);
  const [score, setScore] = useState(0);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showPopUp, setShowPopUp] = useState(false);
  const navigate = useNavigate();

  // Fetch question from API
  const fetchQuestion = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/banana");
      setQuestion(res.data.question);
      setAnswer(res.data.solution);
      setFeedback("");
      setIsCorrect(null);
      setShowPopUp(false);
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const saveScore = async (updatedScore) => {
    try {
        const token = localStorage.getItem("token");
        const userId = localStorage.getItem("userId");

        if (!token || !userId) {
            console.error("User ID or Token not found");
            return;  // Stop execution if userId or token is missing
        }

        const response = await axios.post("http://localhost:5000/api/score/save", 
            { 
                userId, 
                score: updatedScore,  // Pass the updated score
            }, 
            { headers: { Authorization: token } }
        );

        console.log("Score saved successfully", response.data);  // Log response data
    } catch (err) {
        console.error("Error saving score", err);
    }
};





  // Handle timeouts when time is up
  useEffect(() => {
    if (timeLeft === 0) {
      setFeedback("Time's up! ❌");
      setIsGameOver(true);
    } else {
      const timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [timeLeft]);

  useEffect(() => {
    fetchQuestion();
  }, []);

  const checkAnswer = () => {
    if (parseInt(userAnswer) === parseInt(answer)) {
        setFeedback("Correct! 🎉");
        const updatedScore = score + 10;  // Increment score
        setScore(updatedScore);  // Update the score state
        saveScore(updatedScore);  // Pass the updated score
        setIsCorrect(true);  // Set the correct flag
    } else {
        setFeedback("Wrong! ❌");
        setIsCorrect(false);  // Set the incorrect flag
    }
    setShowPopUp(true);  // Show the pop-up after answer
    setUserAnswer("");  // Reset the input field
    fetchQuestion();  // Fetch the next question
};


  const playAgain = () => {
    setIsGameOver(false);
    setScore(0);
    setTimeLeft(30);
    fetchQuestion();
    setUserAnswer("");
  };

  const goToMainMenu = () => {
    navigate("/mainmenu");
  };

  const nextQuestion = () => {
    setShowPopUp(false);
    fetchQuestion();
  };

  return (
    <div className="game-container">
      <h2>Math Game</h2>
      <p>Time Left: {timeLeft}s</p>
      <p>Score: {score}</p>

      {/* Question Image */}
      <img src={question} alt="Math Question" style={{ maxWidth: "300px" }} />

      {/* Answer Input */}
      <input
        type="number"
        value={userAnswer}
        onChange={(e) => setUserAnswer(e.target.value)}
        disabled={isGameOver}
      />
      <button onClick={checkAnswer} disabled={isGameOver}>
        Submit
      </button>

      {/* Feedback */}
      <p>{feedback}</p>

      {/* Main Menu Button */}
      <button onClick={goToMainMenu}>Main Menu</button>

      {/* Correct / Incorrect Answer Pop-up */}
      {showPopUp && (
        <div className="popup">
          {isCorrect ? (
            <>
              <p>Heyyy! Congratulations 🎉</p>
              <p>Points earned: {score}</p>
              <button onClick={nextQuestion}>Next Question</button>
            </>
          ) : (
            <>
              <p>Oops! You lose! ❌</p>
              <button onClick={playAgain}>Try Again</button>
            </>
          )}
        </div>
      )}

      {/* Time's Up Pop-up */}
      {isGameOver && !showPopUp && (
        <div className="popup">
          <p>Time's up! ❌</p>
          <p>Your score: {score} points</p>
          <button onClick={playAgain}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default Game;
