import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { fetchQuestion, saveScore } from "../services/gameService";
import "../styles/Game.css";
import { Menu } from "lucide-react";
import { MusicContext } from "../context/MusicContext";

function Game() {
  const { isMusicOn } = useContext(MusicContext);
  const [difficulty] = useState(localStorage.getItem("selectedDifficulty") || "Easy");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [timeLeft, setTimeLeft] = useState(difficulty === "Easy" ? 30 : difficulty === "Medium" ? 20 : 10);
  const [score, setScore] = useState(0);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showPopUp, setShowPopUp] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchNewQuestion();
    const timer = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
    return () => clearInterval(timer);
  }, []);

  const fetchNewQuestion = async () => {
    try {
      const data = await fetchQuestion();
      setQuestion(data.question);
      setAnswer(data.solution);
      setFeedback("");
      setIsCorrect(null);
      setShowPopUp(false);
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const handleNumberClick = (num) => {
    setUserAnswer((prev) => prev + num);
  };

  const checkAnswer = async () => {
    if (parseInt(userAnswer) === parseInt(answer)) {
      const updatedScore = score + 10;
      setScore(updatedScore);
      await saveScore(updatedScore);
      setIsCorrect(true);
    } else {
      setIsCorrect(false);
    }
    setShowPopUp(true);
    setUserAnswer("");
    fetchNewQuestion();
  };

  const resetGame = () => {
    setIsGameOver(false);
    setScore(0);
    setTimeLeft(30);
    fetchNewQuestion();
    setUserAnswer("");
  };

  return (
    <div>
    <h1>Game Page</h1>
    <p>Music is {isMusicOn ? "ON üéµ" : "OFF üîá"}</p>
  
    <div className="game-container-cartoonic">
      <div className="overlay"></div>
      <button className="main-menu-btn" onClick={() => navigate("/mainmenu")}>
        <Menu size={36} color="white" />
      </button>

      <h2> FIND THE VALUE OF THE BANANA </h2>

      <div className="status-block">
        <div className="s1">Time Left: <span>{timeLeft}s</span></div>
        <div className="s2">Score: <span>{score}</span></div>
      </div>

      <div className="question-board">
        <img src={question} alt="Math Question" className="question-image" />
      </div>

      <div className="number-blocks">
        {[...Array(10).keys()].map((num) => (
          <button key={num} className="number-block" onClick={() => handleNumberClick(num)} disabled={isGameOver}>
            {num}
          </button>
        ))}
      </div>

      <button className="cartoonic-button-3da" onClick={checkAnswer} disabled={!userAnswer}>Submit</button>

      {showPopUp && (
        <div className="popup">
          {isCorrect ? (
            <>
              <p>üéâ Correct! Great Job! üéâ</p>
              <p>Points earned: {score}</p>
              <button onClick={fetchNewQuestion}>Next Question</button>
            </>
          ) : (
            <>
              <p>‚ùå Oops! Try Again! ‚ùå</p>
              <button onClick={resetGame}>Play Again</button>
            </>
          )}
        </div>
      )}

      {isGameOver && !showPopUp && (
        <div className="popup">
          <p>‚è∞ Time's up! ‚è∞</p>
          <p>Your score: {score} points</p>
          <button onClick={resetGame}>Play Again</button>
        </div>
      )}
    </div>
    </div>
  );
}

export default Game;
