import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Game from "./components/Game";
import Home from "./Pages/Home";
import Leaderboard from "./components/Leaderboard";
import MainMenu from "./components/MainMenu"; // Import MainMenu Component
import ChooseDifficulty from "./components/ChooseDifficulty";
import backgroundMusic from "./assets/background.mp3";

export const MusicContext = React.createContext();

function App() {
  const [difficulty, setDifficulty] = useState("");
  const [isMusicOn, setIsMusicOn] = useState(false);
  const audio = React.useRef(new Audio(backgroundMusic));

  useEffect(() => {
    const music = audio.current;
    music.loop = true; // Ensure looping

    if (isMusicOn) {
      music.play().catch((error) => console.error("Audio playback failed", error));
    } else {
      music.pause();
      music.currentTime = 0; // Reset music position
    }

    return () => {
      music.pause();
    };
  }, [isMusicOn]);

  return (
    <MusicContext.Provider value={{ isMusicOn, setIsMusicOn }}>
      <Router>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/choose-difficulty" element={<ChooseDifficulty />} />
          <Route path="/mainmenu" element={<MainMenu />} />
          <Route path="/game" element={<Game />} />
          <Route path="/leaderboard" element={<Leaderboard />} />
        </Routes>
      </Router>
    </MusicContext.Provider>
  );
}

export default App;
