// Leaderboard.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; 
import axios from "axios";
import "../styles/Leaderboard.css";
import { ArrowLeftCircle } from "lucide-react";

function Leaderboard() {
    const [leaderboard, setLeaderboard] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchLeaderboard();
    }, []);

    const fetchLeaderboard = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/score/leaderboard");
            setLeaderboard(res.data);
        } catch (err) {
            console.error("Error fetching leaderboard", err);
        }
    };

    return (
        <div className="leaderboard-container">
            <div className="overlayL"></div>

            <button className="go-back-btn" onClick={() => navigate("/mainmenu")}> 
                <ArrowLeftCircle size={40} strokeWidth={2.5} color="white" />
            </button>

            <h2 className="leaderboard-title">Leaderboard</h2>
            {leaderboard.length > 0 ? (
                <table className="leaderboard-table">
                    <thead>
                        <tr>
                            <th>Rank</th>
                            <th>Player</th>
                            <th>Score</th>
                            <th>Difficulty</th>
                        </tr>
                    </thead>
                    <tbody>
                        {leaderboard
                            .sort((a, b) => b.score - a.score)
                            .map((entry, index) => (
                                <tr key={index} className="leaderboard-row">
                                    <td>{index + 1}</td>
                                    <td>{entry.userId.username}</td>
                                    <td>{entry.score} points</td>
                                    <td>{entry.difficulty}</td>
                                </tr>
                            ))}
                    </tbody>
                </table>
            ) : (
                <p className="no-scores">No scores yet.</p>
            )}
        </div>
    );
}

export default Leaderboard;
