import React from "react";
import { Link } from "react-router-dom";
import '../styles/MainMenu.css';  // Import the new CSS for MainMenu

function MainMenu() {
  return (
    <div className="main-menu">
      <div className="overlayM"></div> {/* Dark overlay for better text visibility */}
      <h2>Welcome to the Main Menu</h2>
      <Link to="/leaderboard">
        <button className="gaming-btn leaderboard-btn">Leaderboard</button>
      </Link>
      <Link to="/game">
        <button className="gaming-btn play-btn">Play Game</button>
      </Link>
      <Link to="/">
        <button className="gaming-btn back-home-btn">Back to Home</button>
      </Link>
    </div>
  );
}

export default MainMenu;
