// MainMenu.js
import React from "react";
import { useNavigate } from "react-router-dom";
import '../styles/MainMenu.css';
import { ArrowLeftCircle } from "lucide-react";
import profileImage from "../assets/images/profile-icon.png"; // Add your profile icon image path


function MainMenu() {
  const navigate = useNavigate();
  const playerName = localStorage.getItem("username") || "Player";

  return (
    <div className="main-menu-cartoonic">
      <div className="overlayM"></div>
      <button className="go-back-button" onClick={() => navigate("/")}> 
        <ArrowLeftCircle size={32} strokeWidth={3} color="white" />
      </button>

        {/* Profile Icon and Player Name */}
        <div className="profile-container">
        <img src={profileImage} alt="Profile" className="profile-icon" />
        <p className="player-name">{playerName}</p>
      </div>

      <h2 className="cartoonic-title"></h2>
      <button className="cartoonic-button-3dd leaderboard-btn" onClick={() => navigate("/leaderboard")}>üèÜ LEADERBOARD</button>
      <button className="cartoonic-button-3dd play-btn" onClick={() => navigate("/choose-difficulty")}>üöÄ  PLAY</button>
      <button className="cartoonic-button-3dd back-home-btn" onClick={() => navigate("/")}>üè° QUIT</button>
    </div>
  );
}

export default MainMenu;
