// Game.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import '../styles/Game.css';
import { Menu } from "lucide-react";
import bananaIcon from "../assets/images/banana-human.png";

function Game() {
  const [difficulty, setDifficulty] = useState(localStorage.getItem("selectedDifficulty") || "Easy");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [timeLeft, setTimeLeft] = useState(30);
  const [score, setScore] = useState(0);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showPopUp, setShowPopUp] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (difficulty === "Easy") setTimeLeft(30);
    if (difficulty === "Medium") setTimeLeft(20);
    if (difficulty === "Hard") setTimeLeft(10);
    fetchQuestion();
  }, [difficulty]);

  const fetchQuestion = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/banana");
      setQuestion(res.data.question);
      setAnswer(res.data.solution);
      setFeedback("");
      setIsCorrect(null);
      setShowPopUp(false);
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const saveScore = async (updatedScore) => {
    try {
      const token = localStorage.getItem("token");
      const userId = localStorage.getItem("userId");
      const difficulty = localStorage.getItem("selectedDifficulty") || "Easy";

      if (!token || !userId) {
        console.error("User ID or Token not found");
        return;
      }

      await axios.post("http://localhost:5000/api/score/save", {
        userId,
        score: updatedScore,
        difficulty
      }, {
        headers: { Authorization: token }
      });
    } catch (err) {
      console.error("Error saving score", err);
    }
  };

  useEffect(() => {
    if (timeLeft === 0) {
      setFeedback("Time's up! ‚ùå");
      setIsGameOver(true);
    } else {
      const timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [timeLeft]);

  const handleNumberClick = (num) => {
    setUserAnswer(prev => prev + num);
  };

  const checkAnswer = () => {
    if (parseInt(userAnswer) === parseInt(answer)) {
      const updatedScore = score + 10;
      setScore(updatedScore);
      saveScore(updatedScore);
      setIsCorrect(true);
    } else {
      setIsCorrect(false);
    }
    setShowPopUp(true);
    setUserAnswer("");
    fetchQuestion();
  };

  const playAgain = () => {
    setIsGameOver(false);
    setScore(0);
    setTimeLeft(30);
    fetchQuestion();
    setUserAnswer("");
  };

  const goToMainMenu = () => {
    navigate("/mainmenu");
  };

  const nextQuestion = () => {
    setShowPopUp(false);
    fetchQuestion();
  };

  return (
    <div className="game-container-cartoonic">
      <div className="overlay"></div> {/* Transparent Dark Overlay */}
      <button className="main-menu-btn" onClick={goToMainMenu}> <Menu size={36} strokeWidth={3} color="white" /> </button>
     
     
      <h2> FIND THE VALUE OF THE BANANA </h2>

      <div className="status-block">
        <div className="s1">Time Left: <span>{timeLeft}s</span></div>
        <div className="s2">Score: <span>{score}</span></div>
      </div>

      <div className="question-board">
        <img src={question} alt="Math Question" className="question-image" />
      </div>

      <div className="number-blocks">
        {[...Array(10).keys()].map((num) => (
          <button 
            key={num} 
            className={`number-block ${userAnswer.includes(num.toString()) ? 'selected' : ''}`} 
            onClick={() => handleNumberClick(num)}
            disabled={isGameOver}
          >
            {num}
          </button>
        ))}
      </div>

      <button className="cartoonic-button-3da" onClick={checkAnswer} disabled={isGameOver || userAnswer === ""}>Submit</button>

      {showPopUp && (
        <div className="popup">
          {isCorrect ? (
            <>
              <p>üéâ Correct! Great Job! üéâ</p>
              <p>Points earned: {score}</p>
              <button onClick={nextQuestion}>Next Question</button>
            </>
          ) : (
            <>
              <p>‚ùå Oops! Try Again! ‚ùå</p>
              <button onClick={playAgain}>Play Again</button>
            </>
          )}
        </div>
      )}

      {isGameOver && !showPopUp && (
        <div className="popup">
          <p>‚è∞ Time's up! ‚è∞</p>
          <p>Your score: {score} points</p>
          <button onClick={playAgain}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default Game;
