import '../styles/Game.css';  // Import the ChooseDifficulty CSS
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // For navigation after the time is up

function Game() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [timeLeft, setTimeLeft] = useState(30); // Default time for Easy level (should be set dynamically based on level)
  const [score, setScore] = useState(0);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null); // To track whether answer is correct or not
  const [showPopUp, setShowPopUp] = useState(false); // To control pop-up visibility
  const navigate = useNavigate(); // For navigation

  // Function to handle timeouts when time is up
  useEffect(() => {
    if (timeLeft === 0) {
      setFeedback("Time's up! ‚ùå");
      setIsGameOver(true); // When time is up, stop the game
    } else {
      const timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
      return () => clearInterval(timer); // Cleanup the timer
    }
  }, [timeLeft]);

  useEffect(() => {
    fetchQuestion(); // Initial question fetch
  }, []);

  // Fetch question from API
  const fetchQuestion = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/banana");
      setQuestion(res.data.question);
      setAnswer(res.data.solution);
      setFeedback("");
      setIsCorrect(null); // Reset the result status
      setShowPopUp(false); // Hide the pop-up
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  // Check if the answer is correct
  const checkAnswer = () => {
    if (parseInt(userAnswer) === parseInt(answer)) {
      setIsCorrect(true);
      setFeedback("Correct! üéâ");
      setScore(score + 10); // Increase score by 10
    } else {
      setIsCorrect(false);
      setFeedback("Wrong! ‚ùå");
    }
    setShowPopUp(true); // Show the pop-up after answering
  };

  // Handle play again after "Time's Up!" or incorrect answer
  const playAgain = () => {
    setIsGameOver(false);
    setScore(0);
    setTimeLeft(30); // Reset time to default (could be dynamic based on difficulty)
    fetchQuestion(); // Fetch new question
    setUserAnswer("");
  };

  // Handle navigation to Main Menu
  const goToMainMenu = () => {
    navigate("/mainmenu");
  };

  // Handle going to next question
  const nextQuestion = () => {
    setShowPopUp(false);
    fetchQuestion(); // Fetch new question after correct answer
  };

  return (
    <div className="game-container">
      <h2>Math Game</h2>
      <p>Time Left: {timeLeft}s</p>
      <p>Score: {score}</p>

      {/* Question Image */}
      <img src={question} alt="Math Question" style={{ maxWidth: "300px" }} />

      {/* Answer Input */}
      <input
        type="number"
        value={userAnswer}
        onChange={(e) => setUserAnswer(e.target.value)}
        disabled={isGameOver} // Disable input if game is over
      />
      <button onClick={checkAnswer} disabled={isGameOver}>
        Submit
      </button>

      {/* Feedback */}
      <p>{feedback}</p>

      {/* Main Menu Button */}
      <button onClick={goToMainMenu}>Main Menu</button>

      {/* Correct / Incorrect Answer Pop-up */}
      {showPopUp && (
        <div className="popup">
          {isCorrect ? (
            <>
              <p>Heyyy! Congratulations üéâ</p>
              <p>Points earned: {score}</p>
              <button onClick={nextQuestion}>Next Question</button>
            </>
          ) : (
            <>
              <p>Oops! You lose! ‚ùå</p>
              <button onClick={playAgain}>Try Again</button>
            </>
          )}
        </div>
      )}

      {/* Time's Up Pop-up */}
      {isGameOver && !showPopUp && (
        <div className="popup">
          <p>Time's up! ‚ùå</p>
          <p>Your score: {score} points</p>
          <button onClick={playAgain}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default Game;
