import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // For navigation after time up


import "../styles/Game.css"; // Import the CSS

function Game() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [difficulty, setDifficulty] = useState("Easy");
  const [timeLeft, setTimeLeft] = useState(30);
  const [score, setScore] = useState(0);
  const [showLeaderboard, setShowLeaderboard] = useState(false);
  const [gameOver, setGameOver] = useState(false); // State for Game Over
  const [timerInterval, setTimerInterval] = useState(null); // Timer interval reference

  useEffect(() => {
    if (!gameOver) {
      fetchQuestion();
      setTimerInterval(setInterval(() => setTimeLeft((prev) => prev - 1), 1000)); // Timer starts
    }
    return () => clearInterval(timerInterval); // Cleanup interval on unmount
  }, [difficulty, gameOver]);

  useEffect(() => {
    if (timeLeft === 0 && !gameOver) {
      clearInterval(timerInterval);
      setGameOver(true); // Set the game over state when time is up
      setFeedback("Time's up! ❌");
    }
  }, [timeLeft]);

  const fetchQuestion = async () => {
    try {
      setTimeLeft(difficulty === "Easy" ? 30 : difficulty === "Medium" ? 20 : 10); // Set time based on difficulty
      const res = await axios.get("http://localhost:5000/api/banana");
      setQuestion(res.data.question);
      setAnswer(res.data.solution);
      setFeedback("");
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const checkAnswer = () => {
    if (parseInt(userAnswer) === parseInt(answer)) {
      setFeedback("Correct! 🎉");
      setScore(score + 10);
    } else {
      setFeedback("Wrong! ❌");
    }
    setUserAnswer("");
    fetchQuestion();
  };

  const playAgain = () => {
    setGameOver(false);
    setScore(0);
    setTimeLeft(difficulty === "Easy" ? 30 : difficulty === "Medium" ? 20 : 10); // Reset timer
    setUserAnswer("");
    setFeedback("");
    setTimerInterval(setInterval(() => setTimeLeft((prev) => prev - 1), 1000)); // Restart timer
  };

  return (
    <div className="game-container">
      <h2>Math Game</h2>
      {!gameOver ? (
        <>
          <label>Choose Difficulty: </label>
          <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
            <option value="Easy">Easy</option>
            <option value="Medium">Medium</option>
            <option value="Hard">Hard</option>
          </select>

          <p>Time Left: {timeLeft}s</p>
          <p>Score: {score}</p>

          <img src={question} alt="Math Question" style={{ maxWidth: "300px" }} />

          <input type="number" value={userAnswer} onChange={(e) => setUserAnswer(e.target.value)} />
          <button onClick={checkAnswer}>Submit</button>

          <p>{feedback}</p>
        </>
      ) : (
        <div className="game-over-popup">
          <h3>Time's Up! ❌</h3>
          <p>Your final score: {score}</p>
          <button onClick={playAgain} className="play-again-btn">Play Again</button>
        </div>
      )}
    </div>
  );
}

export default Game;
