import React, { useState, useEffect } from "react";
import axios from "axios";
import ChooseDifficulty from "./ChooseDifficulty";  // Import the Difficulty Modal

function Game() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);
  const [difficulty, setDifficulty] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState("");
  const [showDifficultyModal, setShowDifficultyModal] = useState(true);

  useEffect(() => {
    if (difficulty) {
      fetchQuestion();
    }
  }, [difficulty]);

  const fetchQuestion = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/banana");  // Assuming this returns a math question
      setQuestion(res.data.question);
      setAnswer(res.data.solution);
      setFeedback("");
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const checkAnswer = () => {
    if (parseInt(userAnswer) === parseInt(answer)) {
      setFeedback("Correct! 🎉");
      setScore(score + 10);
      setPopupMessage(`Heyyy! Congratulations 🎉 You earned ${10} points.`);
      setShowPopup(true);  // Show success popup
    } else {
      setFeedback("Wrong! ❌");
      setPopupMessage("Oops! You lose. ❌ Try again!");
      setShowPopup(true);  // Show failure popup
    }
    setUserAnswer("");
  };

  const handleNext = () => {
    setShowPopup(false);
    fetchQuestion();  // Fetch the next question
  };

  const handleRetry = () => {
    setShowPopup(false);
    fetchQuestion();  // Retry fetching the question
  };

  const handleDifficultySelect = (selectedDifficulty) => {
    setDifficulty(selectedDifficulty);
    setShowDifficultyModal(false);  // Close the difficulty modal
  };

  return (
    <div>
      {showDifficultyModal && (
        <ChooseDifficulty onClose={() => setShowDifficultyModal(false)} onSelect={handleDifficultySelect} />
      )}

      <h2>Math Game</h2>
      <p>Difficulty: {difficulty}</p>
      <p>Score: {score}</p>

      {question && (
        <>
          <img src={question} alt="Math Question" style={{ maxWidth: "300px" }} />
          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
          />
          <button onClick={checkAnswer}>Submit</button>
        </>
      )}

      <p>{feedback}</p>

      {/* Show pop-up for correct or incorrect answer */}
      {showPopup && (
        <div className="popup">
          <p>{popupMessage}</p>
          {popupMessage.includes("Congratulations") ? (
            <button onClick={handleNext}>Next</button>
          ) : (
            <button onClick={handleRetry}>Try Again</button>
          )}
        </div>
      )}
    </div>
  );
}

export default Game;
