import React, { useState, useEffect } from "react";
import axios from "axios";
import Leaderboard from "./Leaderboard"; // Import Leaderboard Component

function Game() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [difficulty, setDifficulty] = useState("Easy");
  const [timeLeft, setTimeLeft] = useState(30);
  const [score, setScore] = useState(0);
  const [showLeaderboard, setShowLeaderboard] = useState(false); // Toggle Leaderboard

  useEffect(() => {
    fetchQuestion();
  }, [difficulty]);

  useEffect(() => {
    if (timeLeft === 0) {
      setFeedback("Time's up! ❌");
      setTimeout(fetchQuestion, 1000);
    }
    const timer = timeLeft > 0 && setInterval(() => setTimeLeft((t) => t - 1), 1000);
    return () => clearInterval(timer);
  }, [timeLeft]);

  const fetchQuestion = async () => {
    try {
      setTimeLeft(difficulty === "Easy" ? 30 : difficulty === "Medium" ? 20 : 10);
      const res = await axios.get("http://localhost:5000/api/banana");
      setQuestion(res.data.question);
      setAnswer(res.data.solution);
      setFeedback("");
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const checkAnswer = () => {
    if (parseInt(userAnswer) === parseInt(answer)) {
        setFeedback("Correct! 🎉");
        const updatedScore = score + 10;  // Increment score
        setScore(updatedScore);  // Update the score state
        saveScore(updatedScore);  // Pass the updated score
    } else {
        setFeedback("Wrong! ❌");
    }
    setUserAnswer("");
    fetchQuestion();
};


const saveScore = async () => {
    try {
        const token = localStorage.getItem("token"); // Get user's auth token
        const userId = localStorage.getItem("userId"); // Get user's id from localStorage
        await axios.post("http://localhost:5000/api/score/save", 
        { 
            userId, 
            score, 
            difficulty 
        }, 
        { headers: { Authorization: token } });

        console.log("Score saved successfully");
    } catch (err) {
        console.error("Error saving score", err);
    }
};

  
  return (
    <div>
      <h2>Math Game</h2>
      <label>Choose Difficulty: </label>
      <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
        <option value="Easy">Easy</option>
        <option value="Medium">Medium</option>
        <option value="Hard">Hard</option>
      </select>

      <p>Time Left: {timeLeft}s</p>
      <p>Score: {score}</p>

      <img src={question} alt="Math Question" style={{ maxWidth: "300px" }} />

      <input type="number" value={userAnswer} onChange={(e) => setUserAnswer(e.target.value)} />
      <button onClick={checkAnswer}>Submit</button>

      <p>{feedback}</p>

      {/* Leaderboard Button */}
      <button onClick={() => setShowLeaderboard(!showLeaderboard)}>
        {showLeaderboard ? "Hide Leaderboard" : "Show Leaderboard"}
      </button>

      {/* Display Leaderboard when button is clicked */}
      {showLeaderboard && <Leaderboard />}
    </div>
  );
}

export default Game;
