import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/signup.css"; // Import the CSS file
import Swal from "sweetalert2";
import { ArrowLeftCircle } from "lucide-react";
import signUpButtonImage from "../assets/images/sign.png"; // Orange button image
import bananaHumanIcon from "../assets/images/banana-human.png"; // Banana human symbol

function Signup() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/users/register", {
        username,
        password,
      });

      Swal.fire({
        title: "Account Created!",
        text: "Welcome! Please log in to continue.",
        icon: "success",
        background: "#1e1e1e",
        color: "#fff",
        confirmButtonText: "OK",
        confirmButtonColor: "#ff6700",
        backdrop: "rgba(0,0,0,0.8)",
      });

      navigate("/login");
    } catch (err) {
      Swal.fire({
        title: "Signup Failed!",
        text: "Username already exists. Please try another.",
        icon: "error",
        background: "#1e1e1e",
        color: "#fff",
        confirmButtonText: "Retry",
        confirmButtonColor: "#ff0000",
        borderRadius: "15px",
        backdrop: "rgba(255, 0, 0, 0.1)",
      });
    }
  };

  return (
    <div className="signup-container">
      <button className="go-back-button" onClick={() => navigate("/")}>
        <ArrowLeftCircle size={32} strokeWidth={3} color="white" />
      </button>

      <div className="overlay"></div> {/* Background Overlay */}
      <div className="signup-box">
        <img
          src={bananaHumanIcon}
          alt="Banana Human"
          className="banana-icon"
        /> {/* Banana Human Icon */}
        <h2>Sign Up</h2>
        <form onSubmit={handleSignup}>
          <input
            type="text"
            placeholder="Enter Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="glowing-button">
            <img src={signUpButtonImage} alt="Sign Up" className="button-bg" />
            <span>Sign Up</span>
          </button>
        </form>
        <p className="login-text">Already have an account? </p>
        <span className="span" onClick={() => navigate("/login")}>
          Log In
        </span>
      </div>
    </div>
  );
}

export default Signup;
