import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/signup.css"; // Import the CSS file
import Swal from "sweetalert2";

function Signup() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();


  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/users/register", {
        username,
        password,
      });

      // Success Alert
      Swal.fire({
        title: "Account Created!",
        text: "Your account has been successfully created. Please log in!",
        icon: "success",
        confirmButtonText: "OK",
        confirmButtonColor: "#4db8ff",
      });

      navigate("/login");
    } catch (err) {
      // Check if the error is due to duplicate username
      if (err.response && err.response.status === 400) {
        Swal.fire({
          title: "Username Already Exists",
          text: "Please choose a different username.",
          icon: "error",
          background: "#222",
          color: "#fff",
          confirmButtonText: "Try Again",
          confirmButtonColor: "#ff4757",
          customClass: {
            popup: "glow-alert",
          },
        });
      } else {
        Swal.fire({
          title: "Signup Failed!",
          text: "An unexpected error occurred. Try again later.",
          icon: "error",
          confirmButtonText: "OK",
          confirmButtonColor: "#ff4757",
        });
      }
    }
  };

  return (
    <div className="signup-container">
      <div className="overlay"></div> {/* Background Overlay */}
      <div className="signup-box">
        <h2>Sign Up</h2>
        <form onSubmit={handleSignup}>
          <input
            type="text"
            placeholder="Enter Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="glowing-button">Sign Up</button>
        </form>
        <p className="login-text">Already have an account? </p>
        <span className="span" onClick={() => navigate("/login")}>Log In</span>
      </div>
    </div>
  );
}

export default Signup;
