import React, { createContext, useState } from "react";

export const MusicContext = createContext();

export function MusicProvider({ children }) {
  const [isMusicOn, setIsMusicOn] = useState(true);
  const [audio] = useState(new Audio("/path-to-your-music-file.mp3"));

  const startMusic = () => {
    if (!audio.paused) return; // Prevent restarting if already playing
    audio.loop = true;
    audio.play();
  };

  const stopMusic = () => {
    audio.pause();
    audio.currentTime = 0; // Reset music to the start
  };

  return (
    <MusicContext.Provider value={{ isMusicOn, setIsMusicOn, startMusic, stopMusic }}>
      {children}
    </MusicContext.Provider>
  );
}
